sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var newEmailCat = "0fd8efb81b8325108c5143f3cc4bcbfa";
    var peopleTeamCat = "9559de421b124a5062e50ed2cd4bcb20";
    var noEmailCat = "7ea8c1211bd3c21062e50ed2cd4bcb12";

    var fName;
    var lName;
    var hireDate;
    var query = 'request=' + current.sys_id.toString() +
        '^cat_item' + newEmailCat +
        '^ORcat_item' + noEmailCat +
        '^ORcat_item' + peopleTeamCat;
    var emailReq = new GlideRecord("sc_req_item");
    emailReq.addEncodedQuery(query);
    emailReq.query();
    if (emailReq.next()) {
        fName = emailReq.variables.new_hire_legal_first_name.toString();
        lName = emailReq.variables.new_hire_legal_last_name.toString();
    }
    var peopleReq = new GlideRecord("sc_req_item");
    peopleReq.addQuery("request", current.sys_id);
    peopleReq.addQuery("cat_item", peopleTeamCat);
    peopleReq.query();
    if (peopleReq.next()) {
        hireDate = peopleReq.variables.hire_date;
    }
    current.short_description =
        "New Hire Request - " + fName + " " + lName + " - " + hireDate;
    current.update();
})(current, previous);
