	while(gr_f.next()){
		numbers = numbers + gr_f.number + ' - ' + gr_f.short_description + '\n';
	}
}
else{
	while(gr.next()){
			numbers = numbers + gr.number + ' - ' + gr.short_description + '\n';
	}
}


