function ifScript() {
	var name_full = current.variables.new_hire_legal_first_name + ' ' + current.variables.new_hire_legal_last_name;
	var name_first = current.variables.new_hire_legal_first_name;
	var name_last = current.variables.new_hire_legal_last_name;
	//gr.addEncodedQuery('cat_item=5f918a921b2f9c10f5a3635bbc4bcba9^short_descriptionLIKE'+name);
	gr.addEncodedQuery('cat_item=5f918a921b2f9c10f5a3635bbc4bcba9^short_descriptionLIKE' + name_full +'^ORshort_descriptionLIKE' + name_first + '^ORshort_descriptionLIKE' + name_last + '^active=true');
	gr.query();
	
	//cat_item=5f918a921b2f9c10f5a3635bbc4bcba9^short_descriptionLIKEadam
    if (gr.next()) {
        return 'yes';
    }
    return 'no';
}

