var name = current.variables.new_hire_legal_first_name + ' ' + current.variables.new_hire_legal_last_name;
var taskDesc = 'Add ' + name + ' to Distribution List ';
var companyRef = current.variables.operating_company.getValue();
var empType = current.variables.type.getValue();
var department = current.variables.department.getDisplayValue();

//Look up company
var companyGr = new GlideRecord('core_company');
companyGr.get(companyRef);
var parentCompany = companyGr.parent.getDisplayValue();

if (parentCompany != undefined && parentCompany != ''){
//Start building constructor for object key/property selection
var select1 = '';
var select2 = '';
var select3 = '';
var finalSelection = '';

//3 Level selection process. distributionLookup[company/segment][contractor/non-contractor][Department]
//Level 1 selection
//If APi Group Inc new hire then use company name as segment
if(companyGr.name=='APi Group Inc'){
	select1 = companyGr.name;
}
//If not APi Group Inc new hire then see if the company record has a parent(segment) and use that for level 1 selection
else if(parentCompany != undefined && parentCompany != ''){
	select1 = parentCompany;
}
//Level 2 selection
//If Full-Time or Intern then non-contractor otherwise contractor = contractor
if(empType == 'Contractor'){
	select2 = 'contractor';
}
else if(empType == 'Intern' || empType == 'Full-Time' || empType == 'Part-Time'){
	select2 = 'non-contractor';
}
//Level 3 selection
//Use department name
select3 = department;

//Set final selection via lookup in the object
finalSelection = distributionLookup[select1][select2][select3];

//Finish task description
taskDesc += finalSelection;
if(finalSelection != undefined && finalSelection != ''){
var taskGr = new GlideRecord('sc_task');
taskGr.initialize();
taskGr.assignment_group = '797935531b4b50d0f5a3635bbc4bcbe3';//Messaging
taskGr.short_description = taskDesc;
taskGr.request_item = current.sys_id;
taskGr.requested_for = current.requested_for;
taskGr.insert();
}
}

