current.assignment_group = current.cat_item.group;

//Set short description
var name = current.variables.new_hire_legal_first_name + ' ' + current.variables.new_hire_legal_last_name;
var date = current.variables.hire_date;
var shortDescription = 'New Hire Request - ' + name + ' - ' + date;
current.short_description = shortDescription;

current.description = 'New Active Directory account setup (auto-generates email, assigns standard organizational AD groups, and shared drives based on company/location)'+'\n'+'- Non-standard Active Directory group assignment'+'\n'+'-Non-standard Shared Drive Access'+'\n'+'-Office License Setup (if selected)'+'\n'+'-Distribution List';

var gr = new GlideRecord('sc_request');
gr.addQuery('number',current.request.number);
gr.query();

if(gr.next()){
	gr.short_description = shortDescription;
	gr.update();
}

